const INVALID_JSON_JWS = 'eyJhbGciOiJSUzI1NiJ9..HKBqsw67Znxo46o4KeDbAlgC5-44wDl9uLnB3NzPoHZ01kjwu-P1vZIIsnK6tPJlaRzSezBW-ohGDFdyYV-MpA5mEUbeFTqclArkX4Qox9iNwLFAp2859BRE598avrme25ASbPFdV9aifsHK3FCIoZl_RRTBnRB084mkvjhEDRthptQdpTDGNVdx9kcV_sYyrxHy72dPzNC-kIl_uO7WYgeYK4phfNhRuPQAvapQWuptcNp8DAGb-91A2A6vUkVcCicHRIuUPiUArNhE-0JjHNKlP7-oNHeU2CFr0-qIrf4pRmwCOtkc_TR5p73ZAoGx2rCfOG-QRFKjPTo2qa3dog';

